DROP TABLE ALL_MATCHES;

CREATE TABLE ALL_MATCHES AS
SELECT ST1_2,	ST1_1,	ACE_1,	TPW_2,	TPW_1,	ACE_2,	ST4_2,	ROUND,	DBF_1,	WNR_1,	ST5_2,	ST5_1,	FNL_1,	FNL_2,	ST2_1,	ST2_2,	ST3_1,	ST3_2,	NPA_2,	NPA_1,	SSP_1,	FSP_2,	FSP_1,	SSP_2,	UFE_1,	DBF_2,	BPW_1,	BPW_2,	SSW_1,	SSW_2,	FSW_1,	FSW_2,	WNR_2,	BPC_1,	BPC_2,	UFE_2,	ST4_1,	NPW_1,	NPW_2,	PLAYER2,	PLAYER1,	'CLAY' as COURT,'French Open' AS TOURNAMENT, CASE(RESULT) WHEN 0 THEN PLAYER2 WHEN 1 THEN PLAYER1 END AS WINNER_PLAYER
FROM RAW_FRENCHOPEN_MEN_2013
UNION ALL
SELECT ST1_2,	ST1_1,	ACE_1,	TPW_2,	TPW_1,	ACE_2,	ST4_2,	ROUND,	DBF_1,	WNR_1,	ST5_2,	ST5_1,	FNL1,	FNL2,	ST2_1,	ST2_2,	ST3_1,	ST3_2,	NPA_2,	NPA_1,	SSP_1,	FSP_2,	FSP_1,	SSP_2,	UFE_1,	DBF_2,	BPW_1,	BPW_2,	SSW_1,	SSW_2,	FSW_1,	FSW_2,	WNR_2,	BPC_1,	BPC_2,	UFE_2,	ST4_1,	NPW_1,	NPW_2,	PLAYER2,	PLAYER1,	'CLAY' as COURT, 'Australian Open' AS TOURNAMENT,CASE(RESULT) WHEN 0 THEN PLAYER2 WHEN 1 THEN PLAYER1 END AS WINNER_PLAYER
FROM RAW_AUSOPEN_MEN_2013
UNION ALL
SELECT ST1_2,	ST1_1,	ACE_1,	TPW_2,	TPW_1,	ACE_2,	ST4_2,	ROUND,	DBF_1,	WNR_1,	ST5_2,	ST5_1,	FNL1,	FNL2,	ST2_1,	ST2_2,	ST3_1,	ST3_2,	NPA_2,	NPA_1,	SSP_1,	FSP_2,	FSP_1,	SSP_2,	UFE_1,	DBF_2,	BPW_1,	BPW_2,	SSW_1,	SSW_2,	FSW_1,	FSW_2,	WNR_2,	BPC_1,	BPC_2,	UFE_2,	ST4_1,	NPW_1,	NPW_2,	PLAYER2,	PLAYER1,	'CLAY' as COURT,'US Open' AS TOURNAMENT, CASE(RESULT) WHEN 0 THEN PLAYER2 WHEN 1 THEN PLAYER1 END AS WINNER_PLAYER
FROM RAW_USOPEN_MEN_2013
UNION ALL
SELECT ST1_2,	ST1_1,	ACE_1,	TPW_2,	TPW_1,	ACE_2,	ST4_2,	ROUND,	DBF_1,	WNR_1,	ST5_2,	ST5_1,	FNL_1,	FNL_2,	ST2_1,	ST2_2,	ST3_1,	ST3_2,	NPA_2,	NPA_1,	SSP_1,	FSP_2,	FSP_1,	SSP_2,	UFE_1,	DBF_2,	BPW_1,	BPW_2,	SSW_1,	SSW_2,	FSW_1,	FSW_2,	WNR_2,	BPC_1,	BPC_2,	UFE_2,	ST4_1,	NPW_1,	NPW_2,	PLAYER2,	PLAYER1,	'CLAY' as COURT,'Wimbledon' AS TOURNAMENT, CASE(RESULT) WHEN 0 THEN PLAYER2 WHEN 1 THEN PLAYER1 END AS WINNER_PLAYER
FROM RAW_WIMBLEDON_MEN_2013;
